<script>
export default {
    props: ['activeLink', 'sideMenuActive', 'isAdmin'],
    data() {
        return {
            isAdminAccount: false,
            isUserAccount: true,
        }
    },
    mounted() {
        if (this.isAdmin == true) {
            this.isAdminAccount = true
            this.isUserAccount = false
            setTimeout(() => {
                document.querySelectorAll('.main-aside__list-item--second')[this.activeLink].classList.add('main-aside__list-item--active')

            }, 0)
        } else {
            this.isAdminAccount = false
            this.isUserAccount = true
            setTimeout(() => {

                document.querySelectorAll('.main-aside__list-item--first')[this.activeLink].classList.add('main-aside__list-item--active')
            }, 0)
        }
    },
    watch: {
        sideMenuActive() {
            if (this.sideMenuActive == true) {
                document.querySelector('.main-aside').classList.add('main-aside--active')
            } else {
                document.querySelector('.main-aside').classList.remove('main-aside--active')
            }
        }
    }
}
</script>

